// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  item.json.myNewField = 1;
}

return $input.all();

function cleanJson(text) {
  return text
    .replace(/```json/gi, '')
    .replace(/```/g, '')
    .trim();
}

const results = [];

for (const item of items) {
  const outputBlock = item.json.output?.[0];
  const aiText = outputBlock?.content?.[0]?.text;

  if (!aiText) continue;

  let ai;
  try {
    ai = JSON.parse(cleanJson(aiText));
  } catch (e) {
    throw new Error('Invalid AI JSON: ' + aiText);
  }

  results.push({
    json: {
      comment_id: outputBlock.id,                
      comment_text: ai.comment_text,              
      intent: ai.intent,
      toxicity_score: ai.toxicity_score,
      spam: ai.spam,
      bad_words: ai.bad_words,
      flagged:
        ai.toxicity_score > 60 ||
        ai.spam === true ||
        ai.bad_words === true,
      summary: ai.summary,
      comment_created_at: new Date().toISOString()
    }
  });
}

return results;

const items = $input.all();

const stats = {
  total: items.length,
  positive: 0,
  neutral: 0,
  negative: 0,
  flagged: 0,
  spam: 0,
  toxic: 0,
  samples: []
};

for (const item of items) {
  const data = item.json;

  if (data.intent === 'positive') stats.positive++;
  if (data.intent === 'neutral') stats.neutral++;
  if (data.intent === 'negative') stats.negative++;

  if (data.flagged) {
    stats.flagged++;
    if (stats.samples.length < 5) {
      stats.samples.push(`• ${data.comment_text}`);
    }
  }

  if (data.spam) stats.spam++;
  if (data.toxicity_score > 60) stats.toxic++;
}

// Build Slack-friendly message (mrkdwn)
const slackMessage = `
 *Facebook Comment Moderation Report*

 *Last Run Summary*

• *Total Comments:* ${stats.total}
• *Positive:* ${stats.positive}
• *Neutral:* ${stats.neutral}
• *Negative:* ${stats.negative}

 *Flags*
• *Flagged:* ${stats.flagged}
• *Toxic:* ${stats.toxic}
• *Spam:* ${stats.spam}

 *Flagged Comment Samples*
${stats.samples.length ? stats.samples.join('\n') : 'None'}
`.trim();

// IMPORTANT: Slack node expects ONE item
return [
  {
    json: {
      ...stats,
      slack_message: slackMessage
    }
  }
];
